package source;
import java.util.Scanner;
public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter the limit");
		int n=sc.nextInt();
		
		int i;
		for (i=2;i<=n;i=i+2)
		
		{
			System.out.println(i);
		}
		
		

	}

}
